import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { getAlerts } from "../redux/actions/AlertsActions";
import Moment from "react-moment";
import { RuxLog } from "@astrouxds/rux-log/rux-log.js";

class AlertDisplay extends Component {
	constructor(props) {
		super(props);
		this.state = {
			alerts: [],
		};
	}

	componentDidMount(prevProps) {
		if (this.props !== prevProps) {
			this.props.getAlerts();
		}
		// this.updateAlerts();
	}

	convertTime(time) {
		let convTime = new Intl.DateTimeFormat("en-US", {
			year: "numeric",
			month: "2-digit",
			day: "2-digit",
			hour: "2-digit",
			minute: "2-digit",
			second: "2-digit",
		}).format(time);
		return convTime;
	}

	render() {
		// return <div id="alerts">{this.renderAlerts()}</div>;
		return (
			<div className="grid-zone--sidebar">
				<table className="rux-table">
					<tr className="rux-table__column-head">
						<th>Category</th>
						<th>Status</th>
						<th>Message</th>
						<th>Time</th>
					</tr>
					{this.props.alerts.map((alert) => {
						return (
							<tr>
								<td>{alert.errorCategory}</td>
								<td>
									<rux-status status={alert.errorSeverity}></rux-status>
								</td>
								<td>{alert.errorMessage}</td>
								<td>{this.convertTime(alert.errorTime)}</td>
							</tr>
						);
					})}
				</table>
			</div>
		);
	}
}

const mSTP = (state) => {
	return {
		alerts: state.alerts.alerts,
	};
};

const mDTP = (dispatch) => {
	return {
		getAlerts: (info) => dispatch(getAlerts(info)),
	};
};

export default connect(mSTP, mDTP)(AlertDisplay);
