import { AUTH_SUCCESS } from "../actionTypes";
import { LOGOUT } from "../actionTypes";

export default function UsersReducer(
	state = {
		loggedIn: false,
		user: { current: {}, valid: true, authCompleted: false, errors: {} },
	},
	action
) {
	switch (action.type) {
		case AUTH_SUCCESS:
			return {
				...state,
				loggedIn: true,
				user: action.payload.currentUser,
			};

		case LOGOUT:
			return {
				...state,
				loggedIn: false,
				user: {},
			};

		default:
			return state;
	}
}
