export const getAlerts = () => {
	return (dispatch) => {
		fetch("http://localhost:3001/alerts", {
			credentials: "include",
			method: "GET",
			headers: {
				Accept: "application/json",
				"Access-Control-Allow-Origin": true,
			},
		})
			.then((res) => res.json())
			.then((JSON) => {
				dispatch({ type: "SET_ALERTS", alerts: { data: JSON } });
			});
	};
};
