import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { getAlerts } from "../redux/actions/AlertsActions";
import AlertInfo from "./AlertInfo";
import { RuxLog } from "@astrouxds/rux-log/rux-log.js";

class AlertDisplay extends Component {
	constructor(props) {
		super(props);
		this.state = {
			alerts: [],
		};
	}

	componentDidMount(prevProps) {
		if (this.props !== prevProps) {
			this.props.getAlerts();
		}
	}

	renderLogData() {
		let myLogData = [];
		this.props.alerts.alerts.map((alert) => {
			myLogData.push({
				message: alert.errorMessage,
				category: alert.errorCategory,
				timestamp: alert.errorTime,
				status: alert.errorSeverity,
			});
		});
	}
	// renderAlerts() {
	// 	return this.props.alerts.alerts.map(this.renderSingleAlert);
	// }

	// renderSingleAlert = (alertObj) => {
	// 	return (
	// 		<AlertInfo
	// 			key={alertObj.errorId}
	// 			id={alertObj.errorId}
	// 			errorMessage={alertObj.errorMessage}
	// 			errorCategory={alertObj.errorCategory}
	// 			errorTime={alertObj.errorTime}
	// 		/>
	// 	);
	// };

	render() {
		// return <div id="alerts">{this.renderAlerts()}</div>;
		return `<rux-log data=this.renderLogData()></rux-log>`;
	}
}

const mSTP = (state) => {
	return {
		alerts: state.alerts,
	};
};

const mDTP = (dispatch) => {
	return {
		getAlerts: (info) => dispatch(getAlerts(info)),
	};
};

export default connect(mSTP, mDTP)(AlertDisplay);
