import axios from "axios";

export const getAlerts = () => {
	return (dispatch) => {
		dispatch({ type: "START_ADDING_ALERTS" });
		axios
			.get("http://localhost:3001/alerts")
			.then((res) => res.json())
			.then((JSON) => {
				console.log(JSON);
				dispatch({ type: "SET_ALERTS", alerts: JSON });
			})
			.catch((error) => {
				throw error;
			});
	};
};
