import axios from "axios";

export const getAlerts = () => {
	return (dispatch) => {
		dispatch({ type: "START_ADDING_ALERTS" });
		axios
			.get("http://localhost:3001/alerts")
			.then((res) => {
				// .then((JSON) => {
				// 	console.log(JSON);
				dispatch({ type: "SET_ALERTS", alerts: res });
			})
			.catch((error) => {
				throw error;
			});
	};
};
