import React, { Component } from "react";

class SatDisplay extends Component {
	constructor(props) {
		super(props);
		this.state = {
			alerts: [],
		};
	}

	componentDidMount(prevProps) {
		if (this.props !== prevProps) {
			this.props.getAlerts();
		}
		// this.updateAlerts();
	}

	convertTime(time) {
		let convTime = new Intl.DateTimeFormat("en-US", {
			year: "numeric",
			month: "2-digit",
			day: "2-digit",
			hour: "2-digit",
			minute: "2-digit",
			second: "2-digit",
		}).format(time);
		return convTime;
	}
	render() {
		return <div className="grid-zone grid-zone--main"></div>;
	}
}

const mSTP = (state) => {
	return {
		satellites: state.satellites.satellites,
	};
};

const mDTP = (dispatch) => {
	return {
		getSatellites: (info) => dispatch(getSatellites(info)),
	};
};

export default connect(mSTP, mDTP)(SatDisplay);
