import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { getAlerts } from "../redux/actions/AlertsActions";
import moment from "moment";
import { RuxLog } from "@astrouxds/rux-log/rux-log.js";

class AlertDisplay extends Component {
	constructor(props) {
		super(props);
		this.state = {
			alerts: [],
		};
	}

	componentDidMount(prevProps) {
		if (this.props !== prevProps) {
			this.props.getAlerts();
		}
		// this.updateAlerts();
	}

	// updateAlerts() {
	// 	this.setState({
	// 		alerts: [

	// 		],
	// 	});
	// }

	render() {
		// return <div id="alerts">{this.renderAlerts()}</div>;
		return (
			<div>
				<table className="rux-table">
					<tr className="rux-table__column-head">
						<th>Category</th>
						<th>Status</th>
						<th>Message</th>
						<th>Time</th>
					</tr>
					{this.props.alerts.map((alert) => {
						let options = {
							weekday: "narrow",
							hour: "numeric",
							minute: "numeric",
							second: "numeric",
						};
						// const time = new Date(alert.time);
						return (
							<tr>
								<td>{alert.errorCategory}</td>
								<td>
									<rux-status status={alert.errorSeverity}></rux-status>
								</td>
								<td>{alert.errorMessage}</td>
								<td>{moment(alert.time).format()}</td>
							</tr>
						);
					})}
				</table>
			</div>
		);
	}
}

const mSTP = (state) => {
	return {
		alerts: state.alerts.alerts,
	};
};

const mDTP = (dispatch) => {
	return {
		getAlerts: (info) => dispatch(getAlerts(info)),
	};
};

export default connect(mSTP, mDTP)(AlertDisplay);
